
var ra = require("../lib/ruleClassify.js");

new ra(function(q) {
	var ques = process.argv[2];
	ques  =ques.toLowerCase();
	var flag = 0;
	var qtype = -1;
	//console.log(ques);	
    //console.log(q.questionType(ques));
    //
    //console.log(q.classify(ques));	
    var tag1 = q.questionType(ques);
    var tag2 = q.classify(ques);

	if(ques.indexOf("discuss") >= 0 || ques.indexOf("explain") >= 0 || ques.indexOf("describe") >= 0){
		//console.log("yes");
		qtype  = 1;
		flag = 1;
	}

	if(!flag){
		if (tag2.indexOf("DESC") >= 0){
			qtype  = 1;
			flag = 1;		
		}
		else if((tag1.indexOf("WH")>= 0 || tag1.indexOf("CH")>= 0) && (tag2.indexOf("NUM")>= 0 || tag2.indexOf("LOC")>= 0 || tag2.indexOf("ENTY")>= 0 || tag2.indexOf("HUM")>= 0)){
			qtype = 0;
			flag = 1;
		}
		else{
			qtype = 1;
			flag = 1;
		}
	}


	console.log(qtype);	

});
